        "# ============================================\n",
        "# 5) ENCRYPTION HANDLING\n",
        "# ============================================\n",
        "def open_pdf_from_bytes(data: io.BytesIO) -> Tuple[Optional[fitz.Document], Optional[str]]:\n",
        "    \"\"\"\n",
        "    Try to open a PDF and authenticate if needed.\n",
        "    Returns (doc, error_message). If doc is None, reading failed.\n",
        "    \"\"\"\n",
        "    data.seek(0)\n",
        "    try:\n",
        "        # Attempt without password\n",
        "        doc = fitz.open(stream=data.read(), filetype=\"pdf\")\n",
        "        if not doc.needs_pass:\n",
        "            return doc, None\n",
        "\n",
        "        # Needs password → try empty then provided list\n",
        "        tried = ['<empty>']\n",
        "        if doc.authenticate(\"\"):\n",
        "            return doc, None\n",
        "\n",
        "        #for pwd in PASSWORDS:\n",
        "        #    tried.append(pwd)\n",
        "        #    if doc.authenticate(pwd):\n",
        "        #        return doc, None\n",
        "\n",
        "        # Could not open\n",
        "        return None, f\"Encrypted (AES/password) and none of the passwords worked. Tried: {', '.join(tried)}\"\n",
        "    except Exception as e:\n",
        "        return None, f\"Failed to open PDF: {e}\""
