        "# ============================================\n",
        "# 6) MANIFEST CREATION\n",
        "# ============================================\n",
        "def create_manifest(folder_id: Optional[str] = FOLDER_ID) -> List[Dict[str, Any]]:\n",
        "    pdf_files = list_pdfs(folder_id)\n",
        "    manifest = []\n",
        "\n",
        "    for f in tqdm(pdf_files, desc=\"Processing PDFs\"):\n",
        "        file_id = f[\"id\"]\n",
        "        file_name = f[\"name\"]\n",
        "\n",
        "        status = \"ok\"\n",
        "        errors: List[str] = []\n",
        "        headings: List[str] = []\n",
        "        subheadings: List[str] = []\n",
        "        full_text = \"\"\n",
        "        pdf_title = None\n",
        "\n",
        "        # Download\n",
        "        try:\n",
        "            pdf_bytes = download_pdf_bytes(file_id)\n",
        "        except HttpError as he:\n",
        "            status = \"failed\"\n",
        "            errors.append(f\"Drive download error: {he}\")\n",
        "            pdf_bytes = None\n",
        "        except Exception as e:\n",
        "            status = \"failed\"\n",
        "            errors.append(f\"Download exception: {e}\")\n",
        "            pdf_bytes = None\n",
        "\n",
        "        # Parse (if downloaded)\n",
        "        if pdf_bytes is not None:\n",
        "            doc, open_err = open_pdf_from_bytes(pdf_bytes)\n",
        "            if doc is None:\n",
        "                status = \"encrypted_unreadable\" if \"Encrypted\" in (open_err or \"\") else \"failed\"\n",
        "                errors.append(open_err or \"Unknown open error\")\n",
        "            else:\n",
        "                try:\n",
        "                    headings, subheadings, full_text, meta = extract_pdf(doc)\n",
        "                    pdf_title = meta.get(\"pdf_title\")\n",
        "                except Exception as e:\n",
        "                    status = \"failed\"\n",
        "                    errors.append(f\"Parse error: {e}\")\n",
        "                finally:\n",
        "                    try:\n",
        "                        doc.close()\n",
        "                    except Exception:\n",
        "                        pass\n",
        "\n",
        "        # Build tags (even if minimal text—function is resilient)\n",
        "        tags = build_tags(full_text or \"\", headings, subheadings, top_n=TOP_N_TAGS)\n",
        "\n",
        "        # Prefer PDF metadata title if sensible; else name\n",
        "        title_for_manifest = (pdf_title or file_name).strip() or file_name\n",
        "\n",
        "        # Combine headings + subheadings into topics (order preserved, unique)\n",
        "        topics = []\n",
        "        seen = set()\n",
        "        for t in headings + subheadings:\n",
        "            if t not in seen:\n",
        "                seen.add(t)\n",
        "                topics.append(t)\n",
        "\n",
        "        entry = {\n",
        "            \"source\": \"google_drive\",\n",
        "            \"title\": title_for_manifest,\n",
        "            \"url\": f\"https://drive.google.com/file/d/{file_id}/view\",\n",
        "            \"downloadUrl\": f\"https://drive.google.com/uc?id={file_id}&export=download\",\n",
        "            \"description\": f\"Extracted from PDF {file_name}\",\n",
        "            \"topics\": topics,           # your \"topics\" as headings\n",
        "            \"tags\": tags,                 # derived tags\n",
        "            # Non-breaking extras for observability:\n",
        "            \"status\": status,             # \"ok\" | \"encrypted_unreadable\" | \"failed\"\n",
        "            \"errors\": errors,             # [] unless something went wrong\n",
        "        }\n",
        "        manifest.append(entry)\n",
        "\n",
        "    # Save manifest\n",
        "    with open(\"manifest_pdf.json\", \"w\", encoding=\"utf-8\") as f:\n",
        "        json.dump(manifest, f, indent=2, ensure_ascii=False)\n",
        "\n",
        "    print(\"✅ Manifest created:\", \"manifest.json\")\n",
        "    # Quick summary\n",
        "    ok = sum(1 for x in manifest if x[\"status\"] == \"ok\")\n",
        "    enc = sum(1 for x in manifest if x[\"status\"] == \"encrypted_unreadable\")\n",
        "    fail = sum(1 for x in manifest if x[\"status\"] == \"failed\")\n",
        "    print(f\"Files processed: {len(manifest)} | ok: {ok} | encrypted: {enc} | failed: {fail}\")\n",
        "    return manifest\n",
        "\n"
